Console::Console(Lcd* lcd_ptr) :
		lcd_ptr(lcd_ptr) {
	active_window = Lcd::Rect(0, 0, lcd_ptr->GetLcdWidth(), lcd_ptr->GetLcdHeight());
	column = 0;
	row = 0;
	text_color = 0xFFFFFF;
}

void Console::SetWindow(const Lcd::Rect rect) {
	active_window = rect;
	column = 0;
	row = 0;
}

void Console::SetTextColor(const uint16_t color) {
	text_color = ((color << 8) & 0xF80000) | ((color << 5) & 0xFC00) | ((color << 3) & 0xF8);
}

void Console::SetTextColor(const uint32_t color) {
	text_color = color;
}

void Console::SetCursor(const uint16_t column, const uint16_t row) {
	this->column = column;
	this->row = row;
}

void Console::WriteString(const char* string, const uint16_t size) {
	if (size != 16 && size != 48)
		return;
	uint16_t max_column = active_window.width / (size >> 1);
	uint16_t max_row = active_window.height / size;
	for (uint32_t i = 0; i < strlen(string); ++i) {
		if (*(string + i) == '\n' || column >= max_column) {
			column = 0;
			++row;
			i++;
		}
		if (row >= max_row) {
			return;
		}
		DrawChar(string + i, size);
		++column;
	}
}

void Console::WriteValue(const int32_t value, const uint16_t size) {

}

void Console::WriteValue(const float value, const uint16_t size) {

}

void Console::DrawChar(const char* character, const uint16_t size) {
	uint16_t x = active_window.x + column * (size >> 1);
	uint16_t original_x = x;
	uint16_t y = active_window.y + row * size;
	uint16_t size_count = (size / 16 + (size % 16 ? 1 : 0)) * size;
	uint16_t char_num = *character - ' ';
	for (uint16_t i = 0; i < size_count; ++i) {
		uint8_t temp = size == 16 ? Font::ascii_16[char_num][i] : Font::ascii_48[char_num][i];
		for (uint8_t i = 0; i < 8; ++i) {
			if ((temp >> i) & 1) {
				lcd_ptr->FillColor(Lcd::Rect(x, y, 1, 1), text_color);
			}
			if ((++x) - original_x >= (size >> 1)) {
				x = original_x;
				++y;
			}
		}
	}
}

